package com.pm.model;import jakarta.persistence.CascadeType;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.OneToMany;import jakarta.persistence.Table;import java.util.List;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Entity@Table(name = "AUTHOR_TBL")@Data@AllArgsConstructor@NoArgsConstructorpublic class Author {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int authorId;    private String authorName;    private String description;    @OneToMany(targetEntity = Book.class,cascade = CascadeType.ALL, orphanRemoval = true)    @JoinColumn(name="authorId_fk",referencedColumnName = "authorId")    private List<Book> books;}