package com.pm.model;import com.fasterxml.jackson.annotation.JsonBackReference;import jakarta.persistence.Entity;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.ManyToMany;import jakarta.persistence.Table;import java.util.Set;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@Table(name = "COURSE_TBL")@Setter@Getter@AllArgsConstructor@NoArgsConstructorpublic class Course {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int courseId;    private String courseName;    private String description;    @ManyToMany(mappedBy ="courses" ,fetch = FetchType.LAZY)    @JsonBackReference    private Set<Student> students;}