package com.pm.model;import com.fasterxml.jackson.annotation.JsonManagedReference;import jakarta.persistence.CascadeType;import jakarta.persistence.Entity;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.JoinTable;import jakarta.persistence.ManyToMany;import jakarta.persistence.Table;import java.util.Set;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@Table(name = "STUDENT_TBL")@Setter@Getter@AllArgsConstructor@NoArgsConstructorpublic class Student {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int studentId;    private String firstName;    private String lastName;    private String address;    @ManyToMany(fetch = FetchType.LAZY,cascade = CascadeType.ALL)    @JoinTable(name = "student_course",          joinColumns = { @JoinColumn(name = "studId",referencedColumnName = "studentId") },          inverseJoinColumns = { @JoinColumn(name = "courseId",referencedColumnName = "courseId")})    private Set<Course> courses;}