package com.pm.repository;import com.pm.dto.AuthorBookInfoDto;import com.pm.model.Author;import java.util.List;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;public interface AuthorRepository extends JpaRepository<Author,Integer> {    @Query("SELECT new com.pm.dto.AuthorBookInfoDto(a.authorName,b.bookName) FROM Author a JOIN a.books b")    public List<AuthorBookInfoDto> getAuthorBookInfo();}